DECLARE TOTAL INT DEFAULT 10;
DECLARE MESSAGE VARCHAR(64);

SET TOTAL = 0;

BEGIN
	SELECT * FROM employees;
	DELETE FROM departments;
END;

CASE 
WHEN TOTAL < 10 THEN
	SET MESSAGE = 'lesser';
	SET TOTAL = 0;
WHEN TOTAL > 10 THEN
	SET MESSAGE = 'greater';
	SET TOTAL = 1;
ELSE
	SET MESSAGE = 'equal';
END;

START TRANSACTION READ WRITE;
	SET TRANSACTION ISOLATION LEVEL READ COMMITTED READ WRITE;
	DELETE FROM departments;
COMMIT;

